import React, {useState} from 'react'
import { TextField, FormControl, Select, InputLabel, MenuItem, Button } from '@mui/material';

export default function AddPage() {
    const [name, setName] = useState('');
  const [selectedOption, setSelectedOption] = useState('');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDropdownChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = { name, selectedOption };
      // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint URL.
      const response = await axios.post('YOUR_API_ENDPOINT', formData);

      // Handle the API response here if needed.
      console.log('API Response:', response.data);

      // Call the onSubmit function passed as a prop to notify the parent component.
      onSubmit(formData);
    } catch (error) {
      // Handle error if the API call fails.
      console.error('API Error:', error);
    }
  };
  return (
    <form onSubmit={handleSubmit}>
      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <TextField
          label="Name"
          value={name}
          onChange={handleNameChange}
          variant="outlined"
          required
        />
      </FormControl>
      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel id="dropdown-label">Dropdown</InputLabel>
        <Select
          labelId="dropdown-label"
          id="dropdown"
          value={selectedOption}
          onChange={handleDropdownChange}
          label="Dropdown"
          required
        >
          <MenuItem value="">Select an option</MenuItem>
          <MenuItem value="option1">Option 1</MenuItem>
          <MenuItem value="option2">Option 2</MenuItem>
          <MenuItem value="option3">Option 3</MenuItem>
        </Select>
      </FormControl>
      <Button type="submit" variant="contained" color="primary">Submit</Button>
    </form>
  )
}
